func unitTestGraph() {
    
    // Reset graph
    resetGraph()
    
    // Complete graph
    var N int = 140

    // Add nodes
    for i := 0; i < N; i++ {
        addNode()
    }

    // Add connections
    for i := 0; i < N; i++ {
        for j:=0; j < N; j++ {
            if i != j {
                addEdge(i, j, float64(i + j))
            }
        }
    }

	// Check nodes
	if graph.nodes != N {
		println("[TEST FAILED]")
	}

    // Check dijkstra
    for i := 0; i < graph.nodes; i++ {
        for j := 0; j < graph.nodes; j++ {
			if i != j && countPath(dijkstra(i, j)) != 2 || i == j && countPath(dijkstra(i, j)) != 1 {
				println("[TEST FAILED]");
				return
			}
        }
    }
	println("[TEST PASS]")
}

