package main

// Stations
var stations []string

// Graph struct
type Graph struct {
    nodes int
    al [][]int
    weight [][]float64
    deg []int
}

// Build a graph
var graph Graph

/**
 * Add node to a graph
 */
func addNode() int {
    var neighbors []int
    var weights []float64
    graph.al = append(graph.al, neighbors);
    graph.weight = append(graph.weight, weights);
    graph.deg = append(graph.deg, 0)
    graph.nodes++
    return graph.nodes-1;
}

/**
 * Add edge to a graph
 */
func addEdge(from, to int, w float64) {
    graph.al[from] = append(graph.al[from], to)
    graph.weight[from] = append(graph.weight[from], w)
    graph.deg[from]++
}

/**
 * Connect two nodes
 */
func connect(from, to int, w float64) {
    addEdge(from, to, w)
    addEdge(to, from, w)
}

/**
 * Print path
 */
func printPath(path []int) {
    // Index 0 is guaranteed to be there
    if path[0] < 0 {
        println("No path found")
        return
    }
    for i:=0; path[i] >= 0; i++ {
        if i != 0 {
            print(" -> ")
        }
        print(stations[path[i]])
    }
    println()
}

/**
 * Run Dijkstra
 */
func dijkstra(from ,to int) []int {
    // Check if source is same as destination
    if from == to {
        var path []int
        path = append(path, from)
        path = append(path, -1)
        return path
    }

    // Keep track of visited nodes
    var visited []int
    var UNVISITED int = 0
    var VISITING int = 1
    var VISITED int = 2
    for i:=0; i < graph.nodes; i++ {
        visited = append(visited, UNVISITED)
    }

    // Keep track of cost
    var cost[] float64
    var MAX float64 = 99999.;
    for i:=0; i < graph.nodes; i++ {
        cost = append(cost, MAX)
    }

    // Keep track of parents
    var parent[] int
    for i:=0; i < graph.nodes; i++ {
        parent = append(parent, -1)
    }

    // Queue of node
    var queue []int
    var queueLen = 0
    
    // Allocate space in queue
    for i:=0; i < graph.nodes; i++ {
        queue = append(queue, 0)
    }

    // Queue start node
    queue[0] = from
    visited[from] = VISITING
    queueLen = 1

    // Queue from node
    for queueLen > 0 {
        
        // Search for min weight
        var min int = 0;
        for i := 1; i < queueLen; i++ {
            if cost[queue[i]] < cost[queue[min]] {
                min = i
            }
        }
        var top = queue[min]

        // Remove top from queue
        queue[min], queue[queueLen-1] = queue[queueLen-1], queue[min]
        queueLen--

        // Mark it as visited
        visited[top] = VISITED

        // Update and queue neighbors
        for i := 0; i < graph.deg[top]; i++ {
            var neighbor int = graph.al[top][i]
            var newCost float64 = cost[top] + graph.weight[top][i]
            if visited[neighbor] == UNVISITED {
                cost[neighbor] = newCost
                visited[neighbor] = VISITING
                parent[neighbor] = top
                queue[queueLen] = neighbor
                queueLen++
            } else if visited[neighbor] == VISITING {
                if cost[neighbor] > newCost {
                    cost[neighbor] = newCost
                    parent[neighbor] = top
                }
            }
        }
    }

    // Create reverse path
    var pathRev []int

    // If not reachable
    if parent[to] < 0 {
        pathRev = append(pathRev, -1)
        return pathRev
    }

    // If reachable
    var count int = 0
    pathRev = append(pathRev, to)
    count++
    var tmpParent = parent[to]
    for tmpParent >= 0 {
        pathRev = append(pathRev, tmpParent)
        tmpParent = parent[tmpParent]
        count++
    }
    
    // Reverse path
    var path []int
    for i:=count; i > 0; i-- {
        path = append(path, pathRev[i-1])
    }
    path = append(path, -1)
    return path
}

